import React, { useState, useEffect } from "react";
import "./App.css";
import Table from "./Table.js";
import SearchBar from "./SearchBar.js";
import PageNumber from "./pageNumber.js";
import PageSize from "./pageSize.js";

function App() {
  /* Create state:
        - apiData: List containing dictionaries of countries from API.
        - searchQuery: The query parameter that should be added to &search=
        - pageNumber: The page that is requested
  */

  const [apiData, setApiData] = useState([]);
  const [searchQuery, setSearchQuery] = useState(""); // Default = ""
  const [pageNumber, setPageNumber] = useState(1); //Default = Page 1
  const [pageSize, setPageSize] = useState(""); //Default = Size 10
  const [pageCount, setPageCount] = useState(10); // Initialize pageCount with 0

  const handleSearch = (searchTerm) => {
    searchTerm = searchTerm.charAt(0).toUpperCase() + searchTerm.slice(1);
    setSearchQuery(searchTerm);
  }

  const handlePageChange = (newPage) => {
    setPageNumber(newPage);
  }

  const handleSizeChange = (newSize) => {
    if (isNaN(newSize) || newSize <= 0) {
      console.error("Invalid page size:", newSize);
      return;
    }

    const maxValidPageNumber = Math.min(pageCount, Math.ceil(apiData.length / newSize));
    console.log("FAEB", maxValidPageNumber);

    setPageSize(newSize);

    if (pageNumber > maxValidPageNumber) {
      setPageNumber(1);
    }
  }

  useEffect(() => {
    // All parameters are appended to this URL.
    let apiQuery = "https://dhis2-app-course.ifi.uio.no/api?";

    // If searchQuery isn't empty add &search=searchQuery to the API request.
    if (searchQuery) {
      apiQuery = apiQuery + "&search=" + searchQuery;
    }

    // Add what size we are requesting to the API request.
    apiQuery = apiQuery + "&pageSize=" + pageSize;

    // Add what page we are requesting to the API request.
    apiQuery = apiQuery + "&page=" + pageNumber;

    // Query data from API.
    console.log("apiQuery: " + apiQuery);
    console.log("pageSize: " + pageSize);
    console.log("pageNumber: " + pageNumber);

    fetch(apiQuery)
      .then((results) => results.json())
      .then((data) => {
        // Then add response to state.
        setApiData(data);
        setPageCount(data.pager.pageCount);
        console.log("pageCount: " + data.pager.pageCount); // Log the updated pageCount
      });
  }, [searchQuery, pageNumber, pageSize]); // Array containing which state changes that should re-reun useEffect()

  return (
    <div className="App">
      <h1>Country lookup</h1>
      <SearchBar data= {apiData} onSearch={handleSearch} />
      <Table apiData={apiData} />
      <PageNumber 
        onPageChange={handlePageChange}
        pageNumber={pageNumber} // Pass the current page as a prop
        pageCount={pageCount} // Pass the pageCount as a prop
       />
      <PageSize onSizeChange={handleSizeChange}
        pageNumber={pageNumber} // Pass the current page as a prop
        pageCount={pageCount} // Pass the pageCount as a prop
       />
    </div>
  );
}

export default App;
