import React, { useState } from "react";

function transformListToString(inputList) {
    const continentAbbreviations = {
      'Africa': 'AF',
      'Antarctica': 'AN',
      'Asia': 'AS',
      'Europe': 'EU',
      'North America': 'NA',
      'Oceania': 'OC',
      'South America': 'SA'
    };

    return inputList
      .filter(continent => continentAbbreviations[continent])
      .sort()
      .map(continent => continentAbbreviations[continent]);
  }

const ContinentSelector = ({ onContinentChange }) => {
  const continents = ["Africa", "Asia", "Europe", "North America", "Oceania", "South America"];
  const [selectedContinents, setSelectedContinents] = useState([]);

  const handleCheckboxChange = (continent) => {
    // Toggle the selected continent
    if (selectedContinents.includes(continent)) {
      setSelectedContinents(prevSelected => {
        const updatedSelected = prevSelected.filter(c => c !== continent);
        console.log("Unchecked:", updatedSelected);
        return updatedSelected;
      });
    } else {
      setSelectedContinents(prevSelected => {
        const updatedSelected = [...prevSelected, continent];
        console.log("After adding:", updatedSelected);
        return updatedSelected;
      });
    }
  };

    // Call the callback function when checkboxes change
    const handleSelectionChange = () => {
        const transformedList = transformListToString(selectedContinents);
        console.log("After adding:", transformedList);
        onContinentChange(transformedList);
    };

  return (
    <div>
      <h2>Continent Selector</h2>
      {continents.map((continent) => (
        <label key={continent}>
          <input
            type="checkbox"
            value={continent}
            checked={selectedContinents.includes(continent)}
            onChange={() => {
              handleCheckboxChange(continent);
              handleSelectionChange(); // Call the callback here
            }}
          />
          {continent}
        </label>
      ))}
    </div>
  );
};

export default ContinentSelector;
