import React, { useState } from 'react';

function Table(props) {

  const [sortColumn, setSortColumn] = useState('Country');

  const [sortOrder, setSortOrder] = useState({
    Country: '',
    Population: '',
    Growth: '',
    Continent: '',
  });

  const onSortChange = (column) => {

    setSortOrder((prevSortOrder) => ({
      Country: column === 'Country' ? (prevSortOrder.Country === 'ASC' ? 'DESC' : 'ASC') : '',
      Population: column === 'Population' ? (prevSortOrder.Population === 'ASC' ? 'DESC' : 'ASC') : '',
      Growth: column === 'Growth' ? (prevSortOrder.Growth === 'ASC' ? 'DESC' : 'ASC') : '',
      Continent: column === 'Continent' ? (prevSortOrder.Continent === 'ASC' ? 'DESC' : 'ASC') : '',
    }));


    console.log("column:", column, "prevSortOrder.column: ", sortOrder[column]);

    // Determine the new sortOrder for the clicked column
    const currentOrder = sortOrder[column];
    const newOrder = currentOrder === 'ASC' ? 'DESC' : 'ASC';

    // Call the onSortChange callback to send sorting information to the parent component
    props.handleSort(column, newOrder);
  };

  if (!props.apiData.results) {
    // If the API request isn't completed return "loading...""
    return <p>Loading...</p>;
  } else {

    // Extract the data from the API response
    let data = props.apiData.results;


    return (
      <table class="tabbel">
        <thead>
          <tr>
            <th>
              <button onClick={() => onSortChange('Country')}>
                <div>
                  Country
                  {sortOrder.Country === 'ASC' && ' ▲'}
                  {sortOrder.Country === 'DESC' && ' ▼'}
                </div>
              </button>
            </th>
            <th>
              <button onClick={() => onSortChange('Population')}>
                <div>
                  Population
                  {sortOrder.Population === 'ASC' && ' ▲'}
                  {sortOrder.Population === 'DESC' && ' ▼'}
                </div>
              </button>
            </th>
            <th>
              <button onClick={() => onSortChange('Growth')}>
                <div>
                  Growth
                  {sortOrder.Growth === 'ASC' && ' ▲'}
                  {sortOrder.Growth === 'DESC' && ' ▼'}
                </div>
              </button>
            </th>
            <th>
              <button onClick={() => onSortChange('Continent')}>
                <div>
                  Continent
                  {sortOrder.Continent === 'ASC' && ' ▲'}
                  {sortOrder.Continent === 'DESC' && ' ▼'}
                </div>
              </button>
             </th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row.Country}</td>
              <td>{row.Population.toLocaleString()}</td>
              <td>{row.PopulationGrowth}</td>
              <td>{row.Continent}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
}

export default Table;
