import React, { useState } from 'react';

function Table(props) {

  const [sortColumn, setSortColumn] = useState('Country');

  const [sortOrder, setSortOrder] = useState({
    Country: 'neutral',
    Population: 'neutral',
    Growth: 'neutral',
    Continent: 'neutral',
  });

  const handleSort = (column) => {
    setSortOrder((prevSortOrder) => ({
      ...prevSortOrder,
      [column]:
        prevSortOrder[column] === 'asc'
          ? 'desc'
          : prevSortOrder[column] === 'desc'
          ? ''
          : 'asc', // Toggle between asc, desc, and no sort
    }));
  };

  if (!props.apiData.results) {
    // If the API request isn't completed return "loading...""
    return <p>Loading...</p>;
  } else {

    // Extract the data from the API response
    let data = props.apiData.results;


    return (
      <table class="tabbel">
        <thead>
        <tr>
              <th>
                Country{' '}
                <button onClick={() => handleSort('Country')}>
                  {sortOrder.Country === 'asc' ? '▲' : sortOrder.Country === 'desc' ? '▼' : ' '}
                </button>
              </th>
              <th>
                Population{' '}
                <button onClick={() => handleSort('Population')}>
                  {sortOrder.Population === 'asc' ? '▲' : sortOrder.Population === 'desc' ? '▼' : ' '}
                </button>
              </th>
              <th>
                Growth{' '}
                <button onClick={() => handleSort('Growth')}>
                  {sortOrder.Growth === 'asc' ? '▲' : sortOrder.Growth === 'desc' ? '▼' : ' '}
                </button>
              </th>
              <th>
                Continent{' '}
                <button onClick={() => handleSort('Continent')}>
                  {sortOrder.Continent === 'asc' ? '▲' : sortOrder.Continent === 'desc' ? '▼' : ' '}
                </button>
              </th>
            </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row.Country}</td>
              <td>{row.Population.toLocaleString()}</td>
              <td>{row.PopulationGrowth}</td>
              <td>{row.Continent}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
}

export default Table;
