import React, { useState } from "react";

function PageNumber(props) {
  const [pageNumber, setPageNumber] = useState(1);

  const handlePageChange = (newPageNumber) => {
    setPageNumber(newPageNumber);
    props.onPageChange(newPageNumber); 
  };

  const handlePreviousPage = (newPageNumber) => {
    if (props.pageNumber > 1) {
      handlePageChange(props.pageNumber - 1);
    }
  };

  const handleNextPage = () => {
    if (props.pageNumber <= props.pageCount) {
        handlePageChange(props.pageNumber + 1);
    }
  };

  return (
    <div>
      {props.pageNumber > 1 && (
        <button onClick={handlePreviousPage}>Previous</button>
      )}
      <span>Page {props.pageNumber}</span>
      {props.pageNumber < props.pageCount && (
        <button onClick={handleNextPage}>Next</button>
      )}
    </div>
  );
}

export default PageNumber;
