import React, { useState, useEffect } from "react";

const ContinentSelector = ({ onContinentChange }) => {
  const continents = ["Africa", "Asia", "Europe", "North America", "Oceania", "South America"];
  const [selectedContinents, setSelectedContinents] = useState([]);

  const handleCheckboxChange = (continent) => {
    // Toggle the selected continent
    if (selectedContinents.includes(continent)) {
      setSelectedContinents(selectedContinents.filter((c) => c !== continent));
      console.log(selectedContinents)
    } else {
      setSelectedContinents([...selectedContinents, continent]);
    }
  };

  // Notify the parent component (App.js) of the selected continents
  useEffect(() => {
    onContinentChange(selectedContinents);
  }, [selectedContinents, onContinentChange]);

  return (
    <div>
      <h2>Continent Selector</h2>
      {continents.map((continent) => (
        <label key={continent}>
          <input
            type="checkbox"
            value={continent}
            checked={selectedContinents.includes(continent)}
            onChange={() => handleCheckboxChange(continent)}
          />
          {continent}
        </label>
      ))}
    </div>
  );
};

export default ContinentSelector;
