import React, { useState } from "react";

function PageSize(props) {
  const { pageCount, pageNumber } = props;

  const handleSizeChange = (event) => {
    const selectedSize = parseInt(event.target.value);
    props.onSizeChange(selectedSize);
  if (pageNumber > pageCount) {
    props.handlePageChange(1);
  }
  };

  return (
    <div>
        <label for="resPerPage">Results per page:</label>
        <select
        name="resPerPage"
        id="resPerPage"
        onChange={handleSizeChange}
        value={props.PageSize} // Use selectedPageSize here
      >
        <option value={10}>10</option>
        <option value={20}>20</option>
        <option value={50}>50</option>
      </select>
    </div>
  );
}

export default PageSize;
