import React, { useState } from "react";

function PageNumber(props) {
  const [pageNumber, setPageNumber] = useState(1);

  const handleClick = (input) => {
    const selectedPageNumber = input.target.value;
    setPageNumber(selectedPageNumber)
    props.onPageChange(selectedPageNumber);
  };

  const handlePreviousPage = () => {
    if (pageNumber > 1) {
      handlePageChange(pageNumber - 1);
    }
  };

  const handleNextPage = () => {
    // You can add a condition here to limit the maximum page number
    handlePageChange(pageNumber + 1);
  };

  return (
    <div>
      <button onClick={handlePreviousPage}>Previous</button>
      <label htmlFor="resPerPage">Page:</label>
      <select
        name="resPerPage"
        id="resPerPage"
        onChange={(e) => handlePageChange(parseInt(e.target.value))}
        value={pageNumber}
      >
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
        {/* Add more options as needed */}
      </select>
      <button onClick={handleNextPage}>Next</button>
    </div>
  );
}

export default PageNumber;
